var bij_ki =
    'কৃষির মূল উপকরণ হচ্ছে বীজ। বীজ উদ্ভিদ জগতের ধারক ও বাহক। বীজই ফসল উৎপাদনে মুখ্য ভূমিকা পালন করে। সাধারণত আমাদের দেশের চাষিরা নিজ নিজ ফসলের অপেক্ষাকৃত ভালো অংশ পরবর্তী ফসলের বীজ হিসাবে ব্যবহার করে। কিন্তু একই বীজ থেকে বার বার চাষ করলে ফসলের ফলন অনেক হ্রাস পায়। তাই প্রায় ২-৩ বছর পর পর বীজ পরিবর্তন (Seed replacement) খুবই জরুরি।  ';
var bijer_songa =
    'বীজ বলতে আসলে অনেক কিছুই বোঝায়। ফসলের যে কোনো অংশ, দানা অথবা অঙ্গ যেটি অনুরূপ একটি ফসল পুনঃ উৎপাদনে সক্ষম তাকেই বীজ বলে অভিহিত করা যায়। বীজ উদ্ভিদের বংশ বিস্তারের মাধ্যম হিসেবে কাজ করে। উদ্ভিদতাত্ত্বিক বৈশিষ্টাবলি এবং কৃষি কাজের বৈচিত্র্যময় ব্যবহার অনুসারে বীজের সংজ্ঞা দু’রকম হতে পারে। প্রথমত, উদ্ভিদতত্ত্বানুসারে ফুলের পরাগরেণু দ্বারা ডিম্বক নিষিক্ত হবার পর পরিপক্ব ডিম্বককে বীজ বলে। যেমন : ধান, গম, পেঁপে বীজ। দ্বিতীয়ত, কৃষিতত্ত্বানুসারে গাছের যে অংশ বংশ বিস্তারের জন্য ব্যবহৃত হয় তাকেই বীজ বলে। যেমন- আলুর টিউবার, মিষ্টিআলুর লতা, কলার সাকার, কুলের  কুঁড়ি, পাথরকুচির পাতা, বিভিন্ন ফুল গাছের শাখা-প্রশাখা ইত্যাদি।';
var bijerr =
    'বাংলাদেশের বীজ বিধিমালা ১৯৯৮ (The Seed Rules,1998) মোতাবেক বীজের ৪ (চার) টি শ্রেণি বিভাগ করা হয়েছে। এগুলো হচ্ছে-প্রজননবিদের বীজ (Breeder Seed) : গবেষণাগারে বীজ প্রজননবিদের (Breeder) এর বিশেষ তত্ত্বাবধানে নির্বাচিত জাতের বীজের মূল বীজ (Nucleus Seeds) গবেষণা কেন্দ্রের বীজ ব্যাংক (Germplasm Bank) থেকে সংগ্রহপূর্বক পরিবর্ধন করে এই বীজ উৎপাদন করা হয়। এই বীজ খুবই অল্প পরিমাণে উৎপাদিত হয় এবং তা অত্যন্ত সময়সাপেক্ষ। এই বীজ থেকে ভিত্তি বীজ উৎপাদন করা যায়।';
var vitti_bij =
    ' প্রজননবিদের বীজের পরবর্তী স্তর ভিত্তি বীজ যা বীজ প্রযুক্তিবিদদের সরাসরি তত্ত্বাবধানে উৎপাদন করা হয়। আমাদের দেশে মূলত বাংলাদেশ কৃষি উন্নয়ন করর্পোরেশন তাদের নির্ধারিত খামারে কঠোর মান নিয়ন্ত্রণ ব্যবস্থাপনার মাধ্যমে ভিত্তি বীজ উৎপাদন করে। তবে গত কয়েক বছর ধরে কিছু বেসরকারি বীজ কোম্পানি এবং সংস্থাকে সরকারি বিধি বিধান পালন সাপেক্ষে ভিত্তি বীজ উৎপাদনের অনুমতি দেয়া হয়েছে। তারাও গবেষণা কেন্দ্র থেকে প্রজননবিদের বীজ সংগ্রহ করে বীজতত্ত্ববিদদের তত্ত্বাবধানে ভিত্তি বীজ উৎপাদন করছেন। ভিত্তি বীজ জাতীয় বীজ অনুমোদন সংস্থা কর্তৃক অবশ্যই প্রত্যয়িত হয়ে থাকে।';
var protta_bij =
    'ভিত্তি বীজ থেকে প্রত্যয়িত বীজ উৎপাদন করা হয়। বিএডিসির নিজস্ব খামার সমূহে এবং কন্ট্রাক্ট গ্রোয়ার্স জোন সমূহে প্রত্যয়িত বীজ উৎপাদিত হয়। জাতীয় বীজ নীতি অনুযায়ী প্রত্যয়িত বীজ সরকারি বীজ অনুমোদন সংস্থা কর্তৃক প্রত্যয়ন বাধ্যতামূলক নয়। তবে বেসরকারি খাতের বীজ ব্যবসায়ীগণ বীজের মান নিয়ন্ত্রণকল্পে বীজ অনুমোদন সংস্থার মাধ্যমে তাদের উৎপাদিত বীজ পরীক্ষা করিয়ে প্রত্যয়ন পত্র নিতে পারেন।  ';
var man_bij =
    'ভিত্তি বীজ বা প্রত্যয়িত বীজ থেকে মান ঘোষিত বীজ উৎপাদন করা হয়। বিএডিসি কৃষক পর্যায়ে  বিপণনের জন্য যে বিপুল পরিমাণ বীজ উৎপাদন করে তা এই শ্রেণির। বেসরকারি খাতের ছোট বড় কোম্পানিগুলো আজকাল মানঘোষিত বীজ উৎপাদন করে বিপণন করে থাকে। এসব বীজ উৎপাদনে বীজ প্রত্যয়ন এজেন্সির প্রত্যয়নপত্র গ্রহণ বাধ্যতামূলক নয়। তবে বীজের গুণগত মান নিশ্চিত কল্পে এবং কৃষদের স্বার্থ সংরক্ষণের জন্য বীজ উৎপাদন, সংরক্ষণ ও বাজারজাতকরণের যে কোনো পর্যায়ে বীজ প্রত্যায়ন এজেন্সির পরিদর্শকগণ বীজ পরীক্ষা করতে পারেন। নির্ধারিত মান লঙ্ঘিত হলে সরকারি আইন অনুযায়ী জরিমানা ও শাস্তির বিধান রয়েছে।  ';
var unnoto_bij = ' আমাদের দেশের বীজ শিল্পের বহুল প্রচলিত স্লোগান হচ্ছে, ‘ভালো বীজ ভালো ফসল’। ভালো বীজ মানে উন্নতমানের বীজ। মান সম্পন্ন বীজ বিভিন্ন শ্রেণির  হতে পারে যেমন- মৌল বীজ, ভিত্তি বীজ, উন্নত মানের বীজ, হাইব্রিড বীজ ইত্যাদি।\nমান সম্পন্ন বীজ :  মান  সম্পন্ন বীজের বৈশিষ্ট্য সমূহ নিম্নরূপ\nবিশুদ্ধ বীজ (কম পক্ষে ৯৪% বিশুদ্ধতা)।\nউজ্জ্বল, সুন্দর, সতেজ, স্বাভাবিক রঙ এর বীজ।\nপুষ্ট, বড় দানা সম্পন্ন বীজ।\nপোকা ও রোগ মুক্ত বীজ।\nবীজের আর্দ্রতা সর্বোচ্চ ১২% (ধান, গম) অন্যান্য ফসল ১০%।\nগজানোর ক্ষমতা ৭০-৮৫% এর উপরে।';
